<script>
  export default {
    name: 'svg_appearance',
    props: ['nodeContent'],
    data () {
      return {
        appearance1: "",  //引擎盖
        appearance2: "",  //左前翼子板
        appearance3: "",  //左前车门
        appearance4: "",  //左后车门
        appearance5: "",  //左后翼子板
        appearance6: "",  //后尾箱盖
        appearance7: "",  //右后翼子板
        appearance8: "",  //右后车门
        appearance9: "",  //右前车门
        appearance10: "",  //右前翼子板
        appearance11: "",  //车顶
        nodeInfor: this.nodeContent
      }
    },
    components: {

		},
    methods: {
      checkNode () {
        this.nodeInfor.map((val,index) => {
          switch (val.recessive){
            case "无损伤":
              this["appearance" + val.dictValue] = "#fff";
              break;
            case "喷漆":
              this["appearance" + val.dictValue] = "#FFD900";
              break;
            case "钣金":
              this["appearance" + val.dictValue] = "#F60000";
              break;
            case "更换":
              this["appearance" + val.dictValue] = "#ACCFFF";
              break;
          }
        })
      }
    },
    mounted(){

    },
    watch:{
      'nodeInfor': 'checkNode',
    }
  }
</script>

